// IMPORT REACT
import React, { useState } from "react";

// ADDITIONAL IMPORTS

// CREATE COMPONENT
const SignUpForm = () => {
  // Create different state for the signup component
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [error, setError] = useState("");

  // Assuming the passed in password is not equal to the password confirm
  const disable = password !== confirm;

  // Create a handle change method to keep track of changes inside the form
  const handleNameChange = (e) => {
    setName([e.target.value]);
  };
  const handleEmailChange = (e) => {
    setEmail([e.target.value]);
  };
  const handlePasswordChange = (e) => {
    setPassword([e.target.value]);
  };
  const handleConfirmChange = (e) => {
    setConfirm([e.target.value]);
  };
  const handleErrorChange = (e) => {
    setError([e.target.value]);
  };

  // Reder component
  return (
    <div>
      <div className="form-container">
        <form autoComplete="off">
          <label>Name</label>
          <input
            type="text"
            name="Name"
            onChange={(e) => {
              return handleInputChange(e);
            }}
            required
          />
          <label>Email</label>
          <input
            type="email"
            name="Email"
            onChange={(e) => {
              return handleInputChange(e);
            }}
            required
          />
          <label>Password</label>
          <input
            type="password"
            name="Password"
            onChange={(e) => {
              return handleInputChange(e);
            }}
            required
          />
          <label>Confirm</label>
          <input
            type="password"
            name="Confirm"
            onChange={(e) => {
              return handleInputChange(e);
            }}
            required
          />
          <button type="submit" disabled={disable}>
            SIGN UP
          </button>
        </form>
      </div>
      <p className="error-message">&nbsp;</p>
    </div>
  );
};

// EXPORT COMPONENT
export default SignUpForm;
